ve_and_wait engineimages.longhorn.rancher.io","  remove_and_wait settings.longhorn.rancher.io","  # do this one last; manager crashes","  remove_and_wait nodes.longhorn.rancher.io","}","","# Delete driver related workloads in specific order","remove_driver() {","  kubectl -n ${NAMESPACE} delete deployment.apps/longhorn-driver-deployer","  kubectl -n ${NAMESPACE} delete daemonset.apps/longhorn-csi-plugin","  kubectl -n ${NAMESPACE} delete statefulset.apps/csi-attacher","  kubectl -n ${NAMESPACE} delete service/csi-attacher","  kubectl -n ${NAMESPACE} delete statefulset.apps/csi-provisioner","  kubectl -n ${NAMESPACE} delete service/csi-provisioner","  kubectl -n ${NAMESPACE} delete daemonset.apps/longhorn-flexvolume-driver","}","","# Delete all workloads in the namespace","remove_workloads() {","  kubectl -n ${NAMESPACE} get daemonset.apps -o yaml | kubectl delete -f -","  kubectl -n ${NAMESPACE} get deployment.apps -o yaml | kubectl delete -f -","  kubectl -n ${NAMESPACE} get replicaset.apps -o yaml | kubectl delete -f -","  kubectl -n ${NAMESPACE} get statefulset.apps -o yaml | kubectl delete -f -","  kubectl -n ${NAMESPACE} get pods -o yaml | kubectl delete -f -","  kubectl -n ${NAMESPACE} get service -o yaml | kubectl delete -f -","}","","# Delete CRD definitions with longhorn.rancher.io in the name","remove_crds() {","  for crd in $(kubectl get crd -o jsonpath={.items[*].metadata.name} | tr ' ' '\\n' | grep longhorn.rancher.io); do","    kubectl delete crd/$crd","  done","}","","remove_crd_instances","remove_driver","remove_workloads","remove_crds"],"stylingDirectives":[[{"start":0,"end":11,"cssClass":"pl-c"},{"start":0,"end":2,"cssClass":"pl-c"}],[],[{"start":10,"end":39,"cssClass":"pl-smi"},{"start":21,"end":23,"cssClass":"pl-k"}],[],[{"start":0,"end":15,"cssClass":"pl-en"}],[{"start":2,"end":7,"cssClass":"pl-k"},{"start":12,"end":14,"cssClass":"pl-smi"}],[{"start":6,"end":54,"cssClass":"pl-s"},{"start":6,"end":7,"cssClass":"pl-pds"},{"start":18,"end":30,"cssC